    class ViewPagerAdapter extends FragmentPagerAdapter {
        private final List<Fragment> mFragmentList = new ArrayList<>();
        private final List<String> mFragmentTitleList = new ArrayList<>();

        public ViewPagerAdapter(FragmentManager manager) {
            super(manager);
        }

        @Override
        public Fragment getItem(int position) {
            return mFragmentList.get(position);
        }

        @Override
        public int getCount() {
            return mFragmentList.size();
        }

        public void addFragment(Fragment fragment, String title) {
            mFragmentList.add(fragment);
            mFragmentTitleList.add(title);
        }

        @Override
        public CharSequence getPageTitle(int position) {
            return mFragmentTitleList.get(position);
        }
    }

    public void setViewPager() {

        ViewPagerAdapter adapter = new ViewPagerAdapter(getSupportFragmentManager());
        Fragment fragmentEventDetails = new MyBrandFragment();
        adapter.addFragment(fragmentEventDetails, "MyBrands");
        Fragment fragmentEventPrizes = new SubscribeBrandFragment();
        adapter.addFragment(fragmentEventPrizes, "Subcribe Brand");
        viewPager.setAdapter(adapter);
        viewPager.setCurrentItem(0);


    }
